package graph;import biologicalElements.GraphElementAbstract;import biologicalElements.Pathway;import graph.jung.classes.MyGraph;import gui.MainWindow;public class GraphInstance{    static MainWindow w = MainWindow.getInstance();    static GraphContainer con = GraphContainer.getInstance();    public GraphInstance() {    }        public static MyGraph getMyGraph() {        try {            return con.getPathway(w.getCurrentPathway()).getGraph();        } catch (Exception e) {            return null;        }            }        public static Pathway getPathwayStatic() {        try {        	return con.getPathway(w.getCurrentPathway());        } catch (Exception e) {            return null;        }          }        public Pathway getPathway() {        return con.getPathway(w.getCurrentPathway());    }    /*public Object getPathwayElement(Object element) {        return con.getPathway(w.getCurrentPathway()).getElement(element);    }*/    public GraphContainer getContainer() {        return con;    }    public GraphElementAbstract getSelectedObject() {        return con.getSelectedObject();    }    public void setSelectedObject(GraphElementAbstract selectedObject) {        con.setSelectedObject(selectedObject);    }}