package graph;import biologicalElements.GraphicalElementAbstract;import biologicalElements.Pathway;import graph.jung.classes.MyGraph;import gui.MainWindow;public class GraphInstance {	private static final MainWindow w = MainWindow.getInstance();	private static final GraphContainer con = GraphContainer.getInstance();	private GraphInstance() {	}	public static VanesaGraph getVanesaGraph() {		try {			return con.getPathway(w.getCurrentPathway()).getGraph2();		} catch (Exception e) {			return null;		}	}	public static MyGraph getMyGraph() {		try {			return con.getPathway(w.getCurrentPathway()).getGraph();		} catch (Exception e) {			return null;		}	}	public static Pathway getPathway() {		final String tabName = w.getCurrentPathway();		if (tabName == null) {			return null;		}		return con.getPathway(tabName);	}	public static GraphContainer getContainer() {		return con;	}	public static GraphicalElementAbstract getSelectedObject() {		return con.getSelectedObject();	}	public static void setSelectedObject(GraphicalElementAbstract selectedObject) {		con.setSelectedObject(selectedObject);	}}